# Convert to numpy array
image = np.array(INPUT_IMAGE, dtype=np.float32)
print(f"Input image shape: {image.shape}")
print(f"Input center value: {image[16, 16]}")

# Apply Gaussian blur with sigma=1.519868
sigma = 1.519868
result_scipy = ndimage.gaussian_filter(image, sigma=sigma, mode='reflect')

print(f"\nSciPy gaussian_filter results:")
print(f"Center result: {result_scipy[16, 16]:.6f}")
print(f"Our result: 0.965353")
print(f"SIFT++ expected: 0.958898")
print(f"Diff (scipy vs our): {result_scipy[16, 16] - 0.965353:.6f}")
print(f"Diff (scipy vs SIFT++): {result_scipy[16, 16] - 0.958898:.6f}")

# Also try different boundary modes
modes = ['reflect', 'constant', 'nearest', 'mirror', 'wrap']
print(f"\nTesting different boundary modes:")
for mode in modes:
    result = ndimage.gaussian_filter(image, sigma=sigma, mode=mode)
    print(f"{mode:>8}: {result[16, 16]:.6f} (diff from SIFT++: {result[16, 16] - 0.958898:.6f})") 